INSERT INTO `bio_article` VALUES ('55', '遇见盛图，幸福“油”礼', '盛图', '有人说，幸福是在最好的年纪 与爱人相遇，喜结良缘 有人说，幸福是踌躇满志的时候 创业成功，功成名就 而对于卡车司机来说', 'admin', '/public/uploads/allimg/180613/1-1P613115234412.jpg', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180507/1-1P50G3131a18.gif\" style=\"width: 640px; height: 275px;\" /><br />\r\n	<br />\r\n	　　有人说，幸福是在最好的年纪</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　与爱人相遇，喜结良缘</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　有人说，幸福是踌躇满志的时候</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　创业成功，功成名就</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　而对于卡车司机来说</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　最幸福的事，莫过于在油价上涨时</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　看着燃油不停地加进油箱</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　但却<span style=\"color:#ff0000;\"><strong>免费</strong></span>享受<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180613/1-1P613115449420.gif\" style=\"width: 30px !important; height: 40px !important;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180613/1-1P613115234412.jpg\" style=\"width: 552px; height: 396px;\" /></span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　遇见盛图，幸福&ldquo;<span style=\"color:#ff0000;\">油</span>&rdquo;礼</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　四川现代盛图夏日福利活动火热进行中</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　即刻加入活动，即可免费</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　领取<span style=\"color:#ff0000;\">100元</span>加油卡</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　更有<span style=\"color:#ff0000;\">3000元</span>惊喜好礼等你来拿</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　点击下图，了解更多活动详情</span><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180613/1-1P613115449420.gif\" style=\"font-size: 14px; width: 30px; height: 40px;\" /><br />\r\n	<br />\r\n	<span style=\"font-size:14px;\"><a href=\"http://www.zjmdzc.com/industryNews/107.html\"><img alt=\"\" src=\"/public/uploads/allimg/180613/1-1P613115254557.jpg\" style=\"height: 1062px; width: 660px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/trucksfamily/weixin.gif\" style=\"width: 322px; height: 522px;\" /></a></span><br />\r\n	&nbsp;</div>\r\n', '194', '0', '0', '1528940564', '43');
INSERT INTO `bio_article` VALUES ('56', '“节油有道”神操作，想不省油都难【四川现代创虎讲堂】', '节油有道', '都说买车容易养车难 那么，养车难到底难在哪里？ 保险费、保养费、停车费、路桥费 以及油费等一大波的费用，确实难 可是，很多费用都在所难免 除了 油费 把 油耗 降下来 养车成本自然也就降下来了 那么，如何才能做到高效节油呢？ 往下看，也许你需要 创虎高效驾驶八大秘密招式 怎么样？看完这波神操作 想不省油都难了', 'admin', '/public/uploads/trucksfamily/68/9.jpg', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\"><br />\r\n	<br />\r\n	　　都说买车容易养车难</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　那么，养车难到底难在哪里？</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　保险费、保养费、停车费、路桥费</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　以及油费等一大波的费用，确实难</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　可是，很多费用都在所难免</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　除了<strong><span style=\"color:#ff0000;\">油费</span></strong>&hellip;&hellip;</span><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/trucksfamily/68/1.jpg\" style=\"font-size: 16px; width: 339px; height: 282px;\" /><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　把<strong>油耗</strong><span style=\"color:#ff0000;\">降下来</span></span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　养车成本自然也就降下来了</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　那么，如何才能做到高效节油呢？</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　往下看，也许你需要</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:16px;\">　　创虎高效驾驶八大秘密招式<br />\r\n	<br />\r\n	<img alt=\"向下看\" src=\"/public/uploads/allimg/180614/1-1P614093G3528.gif\" style=\"width: 30px !important; height: 40px  !important;\" /><br />\r\n	<br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/2.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/3.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/4.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/5.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/6.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/7.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/8.jpg\" /><br />\r\n	<img alt=\"四川现代创虎\" src=\"/public/uploads/trucksfamily/68/9.jpg\" /></span></strong></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　怎么样？看完这波神操作</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:16px;\">　　想不省油都难了<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/trucksfamily/weixin.gif\" style=\"width: 322px; height: 522px;\" /></span></div>\r\n', '51', '0', '0', '1529047560', '43');
INSERT INTO `bio_article` VALUES ('57', '激擎夏日 | 四川现代创虎带你征战2018俄罗斯世界杯', '四川现代创虎', '球迷们苦等4年\r\n激动人心的世界杯终于再次来临 虽然比赛尚未开始，但是大家脑海中已经　满是球场上各种进球瞬间的精彩画面　体内血脉偾张，热血沸腾 今天，四川现代创虎就带领大家', 'admin', '/public/uploads/180614/1-1P61415324N55.jpg', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n	<br />\r\n	　　球迷们苦等4年</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　激动人心的世界杯终于再次来临</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　虽然比赛尚未开始，但是大家脑海中已经</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　满是球场上各种进球瞬间的精彩画面</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　体内血脉偾张，热血沸腾</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　今天，四川现代创虎就带领大家</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　提前来了解一下</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:14px;\">　　2018俄罗斯世界杯的精彩赛程<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180614/1-1P614112050Y0.jpg\" style=\"height: 5563px; width: 660px;\" /></span></strong></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　老铁们，看完以上精彩赛程</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　是不是已经按耐不住心中燃烧的激情？</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　不是？别着急，这只是小组赛</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　真正激烈的比赛还得等到淘汰赛</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　所以，赶紧开着创虎多挣点钱</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　等到决赛之时，咱们到现场观战一波<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/trucksfamily/weixin.gif\" style=\"width: 322px; height: 522px;\" /></span></div>\r\n', '51', '0', '0', '1528961444', '43');
INSERT INTO `bio_article` VALUES ('58', '520我爱你 | 来自的卡嫂的真情告白【四川现代创虎故事】', '四川现代创虎', '风里来，雨里去。因为卡车司机经常在外跑，很少有时间在家陪老婆孩子，所以，他们的婚姻在大多数外人眼里，并不被看好。但是，一对来自苏州连云港的创虎车主夫妇，却用行动告诉大家：其实，卡车司机夫妇也很恩爱。', 'admin', '/public/uploads/allimg/180615/1-1P6150919123E.jpg', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"></span></div>\r\n<div>\r\n	<br />\r\n	<span style=\"font-size:14px;\">　　风里来，雨里去。因为卡车司机经常在外跑，很少有时间在家陪老婆孩子，所以，他们的婚姻在大多数外人眼里，并不被看好。但是，一对来自苏州连云港的创虎车主夫妇，却用行动告诉大家：<strong>其实，卡车司机夫妇也很恩爱</strong>。</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615091543952.jpg\" style=\"width: 640px; height: 480px;\" /></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　近日，为了替爱车的丈夫陶先生选择一辆<strong>高效、安全、舒适</strong>的重卡，潘女士花费了不少心思。在经过多方了解以及对比试驾之后，最终选择了一辆<strong><span style=\"color:#ff0000;\">520马力自动挡四川现代创虎重卡</span></strong>送给丈夫。</span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　&ldquo;<strong>选择创虎重卡，我买的不仅仅是车，是对丈夫一生一世的坚守</strong>。我知道丈夫一个人在外开车挣钱特别辛苦，所以想亲自选择一辆好车陪在丈夫的身边，让他无论在寒冬、酷夏还是深夜里都能<span style=\"color:#ff0000;\"><strong>舒服出行，安全回家</strong></span>。&rdquo;潘女士深情地望着丈夫陶先生说，&ldquo;结婚20年了，感觉只是瞬间，我想和他再守500年。&rdquo;</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615091PbW.jpg\" style=\"width: 640px; height: 750px;\" /></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　<strong>说起为什么最终选择了520马力自动挡四川现代创虎</strong>，陶先生表示：&ldquo;我是一个爱车的人，又是跑货物运输的，所以选车的时候我特别注重车辆的动力性能和操控性能，而520马力自动挡四川现代创虎刚好能满足我的要求，它的<strong><span style=\"color:#ff0000;\">发动机技术很成熟，燃油效率高，而且最大扭矩可达2500N&middot;m，爬坡十分给力，而且自动挡操控更轻松</span></strong>，我很喜欢。&rdquo;</span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　买车的时候，潘女士全程陪同丈夫陶先生。所以选择创虎，除了陶先生的喜好，更多的原因来自于潘女士。</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615091R9C8.jpg\" style=\"width: 640px; height: 853px;\" /></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　据陶先生介绍，老婆潘女士特别看重创虎两点：第一个是安全性高，因为之前她在网上看过一些车辆事故，通常创虎车主都是毫发无损，驾驶室非常坚固，而且刹车灵敏，又有液力缓速器等配置。第二个是驾驶环境舒适，买车的时候她都亲自上去试驾感受，创虎重卡的驾驶室空间很大，卧铺、座椅以及空调等配置都非常高，感觉特别好。</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615091U31D.jpg\" style=\"width: 640px; height: 782px;\" /></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　此次老婆潘女士为陶先生选择了520马力自动挡四川现代创虎重卡，让陶先生特别感动，他真真切切地感受到老婆有多么替自己每一次出行担忧。</span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　陶先生对老婆潘女士说：&ldquo;感谢你的支持，无论我走到哪里，你和家都在我的心里，结婚20年，我在外多，在家少，没时间陪你、陪孩子，十分惭愧。但你知道，开车是我的爱好，也是我养家的工作，感谢你能理解。今天你帮我买了创虎，我心里说不出的幸福，以后有它陪着我，你就可以安心守家，我也可以平安赚钱。&rdquo;</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P6150919123E.jpg\" style=\"width: 660px; height: 440px;\" /></span></div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<span style=\"font-size:14px;\">　　520我爱你，看完陶先生与潘女士这对卡车司机夫妇的真情告白，着实令人感动。四川现代祝愿：天下所有卡车司机，都能和自己的爱妻一生相爱相守，驾享幸福生活。</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/trucksfamily/weixin.gif\" style=\"width: 322px; height: 522px;\" /></span></div>\r\n', '92', '0', '0', '1529047503', '43');
INSERT INTO `bio_article` VALUES ('59', '夏日钜惠 | 四川现代创虎四重豪礼重磅来袭', '四川现代创虎', '钜惠嗨翻天，福利享不停,为了平复广大车主酷暑里焦躁不安的心,四川现代给大家送来了,四重重磅豪礼。豪礼一：幸福大减价\r\n看中创虎牵引车很久了，可每次一摸兜里，总发现差点钱，急得都上火了怎么办？', 'admin', '/public/uploads/180615/1-1P615152310214.jpg', '<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><br />\r\n	<br />\r\n	　　<span style=\"color:#ff0000;\"><strong><span style=\"font-size:16px;\">钜惠</span></strong></span>嗨翻天，<strong><span style=\"color:#ff0000;\"><span style=\"font-size:16px;\">福利</span></span></strong>享不停</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　为了平复广大车主酷暑里焦躁不安的心</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　四川现代给大家送来了</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　<strong><span style=\"font-size:16px;\">四重</span></strong>重磅豪礼</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:16px;\">　　豪礼一：幸福大减价</span></strong></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　看中创虎牵引车很久了</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　可每次一摸兜里，总发现差点钱</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　急得都上火了怎么办？</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　快来参加四川现代创虎<span style=\"color:#ff0000;\">夏日钜惠活动</span></span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　幸福价给您，为您降火<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P61509302M64.jpg\" style=\"width: 401px; height: 799px;\" /></span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:16px;\">　　豪礼二：选配钜优惠</span></strong></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　寒冬时，为了省钱没装独立风暖</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　现在酷暑难当，要不要装顶置空调呢</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　装，连独立风暖一起装了</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　因为创虎<span style=\"color:#ff0000;\">选配装置优惠价</span>来啦</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　最低的价格，给您最好的配置</span><br />\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615093104259.jpg\" style=\"width: 401px; height: 799px;\" /></span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:16px;\">　　豪礼三：黄金版福利</span></strong></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　如果您想要一辆定制级炫酷重卡</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　不妨来四川现代试试</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　<span style=\"color:#ff0000;\">　创虎黄金版牵引车福利赠送活动</span>火热来袭</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　仅需交付订金10000元</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　即可助您实现黄金梦想</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　更有<span style=\"color:#ff0000;\">30万公里保养服务</span>免费赠送<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615093341508.jpg\" style=\"height: 798px; width: 401px;\" /></span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<strong><span style=\"font-size:16px;\">　　豪礼四：特惠大惊喜</span></strong></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　440马力创虎牵引车仅售30几万？</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　什么？还有上万贴息！！！</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　必须了解一下，该项活动只针对</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　2017年12月31日前的库存车</span></div>\r\n<div style=\"text-align: center;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: center;\">\r\n	<span style=\"font-size:14px;\">　　售完为止，先到先得哦！<br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/allimg/180615/1-1P615093423502.jpg\" style=\"width: 401px; height: 799px;\" /><br />\r\n	<br />\r\n	<img alt=\"\" src=\"/public/uploads/trucksfamily/weixin.gif\" style=\"width: 322px; height: 522px;\" /></span><br />\r\n	&nbsp;</div>\r\n', '157', '0', '0', '1529047420', '43');
